import React, { useState } from 'react';
import api from '../../api';
import './Admin.css';

const RegisterStudent = () => {
  const [formData, setFormData] = useState({
    username: '', // Assuming username instead of name to match API
    password: '',
    level: 'Junior Secondary',
    class: 'JSS1',
  });
  const [studentId, setStudentId] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const generateStudentId = () => {
    const prefix = formData.level === 'Junior Secondary' ? 'JSS' : 'SSS';
    const classNumber = formData.class.replace(/\D/g, '');
    const randomId = Math.floor(Math.random() * 10000).toString().padStart(4, '0');
    return `${prefix}${classNumber}-${randomId}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const id = generateStudentId();
    try {
      const response = await api.post('/admin/register', {
        ...formData,
        studentId: id,
      });
      setStudentId(id);
      setSuccessMessage(`Student Registered! ID: ${id}`);
      console.log(response.data);
    } catch (error) {
      setErrorMessage('Error registering student, please try again.');
      console.error("There was an error registering the student!", error);
    }
  };

  return (
    <div className="register-student">
      <h1>Register Student</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"  // Changed to match API
          placeholder="Username"
          value={formData.username}
          onChange={handleInputChange}
          required
        />
        <input
          type="password"
          name="password"  // Added password field
          placeholder="Password"
          value={formData.password}
          onChange={handleInputChange}
          required
        />
        <select name="level" value={formData.level} onChange={handleInputChange}>
          <option value="Junior Secondary">Junior Secondary</option>
          <option value="Senior Secondary">Senior Secondary</option>
        </select>
        <select name="class" value={formData.class} onChange={handleInputChange}>
          <option value="JSS1">JSS1</option>
          <option value="JSS2">JSS2</option>
          <option value="JSS3">JSS3</option>
          <option value="SSS1">SSS1</option>
          <option value="SSS2">SSS2</option>
          <option value="SSS3">SSS3</option>
        </select>
        <button type="submit">Register</button>
      </form>
      {successMessage && <p className="success-message">{successMessage}</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {studentId && <p>Generated Student ID: {studentId}</p>}
    </div>
  );
};

export default RegisterStudent;
