import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../styles/style.css";
import logo from "../assets/logo_image.png";

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState("guest");
  const navigate = useNavigate();

  useEffect(() => {
    // Check local storage for login status and role
    const storedAuthStatus = localStorage.getItem("isAuthenticated") === "true";
    const storedUserRole = localStorage.getItem("userRole") || "guest";

    setIsAuthenticated(storedAuthStatus);
    setUserRole(storedUserRole);
  }, []);

  const handleLogin = () => {
    // Example login behavior
    setIsAuthenticated(true);
    setUserRole("student"); // Example: Setting student role
    localStorage.setItem("isAuthenticated", "true");
    localStorage.setItem("userRole", "student");
    navigate("/student"); // Redirect to Student Dashboard
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserRole("guest");
    localStorage.removeItem("isAuthenticated");
    localStorage.removeItem("userRole");
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <img src={logo} alt="New Age School Logo" className="logo" />
      <ul className="nav-links">
        {/* Main Navigation Links */}
        <li><Link to="/">Home</Link></li>
        <li><Link to="/group-discussion">Group Discussion</Link></li>
        <li><Link to="/exercise-gathering">Exercise Gathering</Link></li>
        <li><Link to="/principal-office">Principal Office</Link></li>
        <li><Link to="/award">Award</Link></li>
        <li><Link to="/excursion">Excursion</Link></li>

        {/* Role-Based Conditional Links */}
        {userRole === "student" && <li><Link to="/student">Student Dashboard</Link></li>}
        {userRole === "parent" && <li><Link to="/parent">Parent Dashboard</Link></li>}
        {userRole === "teacher" && <li><Link to="/teacher">Teacher Dashboard</Link></li>}
        {userRole === "counselor" && <li><Link to="/counselor">Counselor Dashboard</Link></li>}
        {userRole === "principal" && <li><Link to="/principal">Principal Dashboard</Link></li>}

        {/* Conditional Login/Logout Buttons */}
        {!isAuthenticated ? (
          <li className="login-link"><button onClick={handleLogin}>Login</button></li>
        ) : (
          <li className="logout-link"><button onClick={handleLogout}>Logout</button></li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
