import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";

// Pages for general navigation
import Home from "./pages/Home";
import GroupDiscussion from "./pages/GroupDiscussion";
import ExerciseGathering from "./pages/ExerciseGathering";
import PrincipalOffice from "./pages/PrincipalOffice";
import AwardNigeria from "./pages/AwardNigeria";
import ExcursionOutdoor from "./pages/ExcursionOutdoor";
import StudentCounselor from "./pages/StudentCounselor";

// Role-based dashboards
import StudentDashboard from "./components/StudentDashboard";
import ParentDashboard from "./components/ParentDashboard";
import TeacherDashboard from "./components/TeacherDashboard";
import CounselorDashboard from "./components/CounselorDashboard";
import PrincipalDashboard from "./components/PrincipalDashboard";

const App = () => {
  const [currentUserRole, setCurrentUserRole] = useState("guest");

  useEffect(() => {
    // Retrieve role from localStorage or default to "guest"
    const storedRole = localStorage.getItem("userRole") || "guest";
    setCurrentUserRole(storedRole);
  }, []);

  return (
    <Router>
      <div className="app">
        <Navbar />
        <div className="main-content">
          <Routes>
            {/* General Navigation */}
            <Route path="/home" element={<Home />} />
            <Route path="/group-discussion" element={<GroupDiscussion />} />
            <Route path="/exercise-gathering" element={<ExerciseGathering />} />
            <Route path="/principal-office" element={<PrincipalOffice />} />
            <Route path="/award-nigeria" element={<AwardNigeria />} />
            <Route path="/excursion-outdoor" element={<ExcursionOutdoor />} />
            <Route path="/student-counselor" element={<StudentCounselor />} />

            {/* Role-Based Dashboards */}
            <Route path="/student" element={<StudentDashboard />} />
            <Route path="/parent" element={<ParentDashboard />} />
            <Route path="/teacher" element={<TeacherDashboard />} />
            <Route path="/counselor" element={<CounselorDashboard />} />
            <Route path="/principal" element={<PrincipalDashboard />} />

            {/* Default Role-Based Routing */}
            <Route
              path="/"
              element={
                currentUserRole === "guest" ? (
                  <Navigate to="/home" replace />
                ) : (
                  <Navigate to={`/${currentUserRole}`} replace />
                )
              }
            />

            {/* Unauthorized Access */}
            <Route path="/unauthorized" element={<h2>Unauthorized Access</h2>} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
