import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/style.css";
import logo from "../assets/logo_image.png";

const Navbar = () => {
  // Track authentication status (replace with actual user auth logic)
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Update to true when logged in

  return (
    <nav className="navbar">
      <img src={logo} alt="New Age School Logo" className="logo" />
      <ul className="nav-links">
        {/* Main Navigation Links */}
        <li><Link to="/home">Home</Link></li>
        <li><Link to="/group-discussion">Group Discussion</Link></li>
        <li><Link to="/exercise-gathering">Exercise Gathering</Link></li>
        <li><Link to="/principal-office">Principal Office</Link></li>
        <li><Link to="/award-nigeria">Award</Link></li>
        <li><Link to="/excursion-outdoor">Excursion</Link></li>
        <li><Link to="/student-counselor">Counselor</Link></li>

        {/* Role-Based Dashboards */}
        <li><Link to="/student">Student Dashboard</Link></li>
        <li><Link to="/parent">Parent Dashboard</Link></li>
        <li><Link to="/teacher">Teacher Dashboard</Link></li>
        <li><Link to="/counselor">Counselor Dashboard</Link></li>
        <li><Link to="/principal">Principal Dashboard</Link></li>

        {/* Admin Dashboard */}
        {isAuthenticated && (
          <li><Link to="/admin">Admin Dashboard</Link></li>
        )}

        {/* Conditional Login/Logout */}
        {!isAuthenticated ? (
          <li className="login-link">
            <Link to="/login">Login</Link>
          </li>
        ) : (
          <li className="logout-link">
            <Link to="/logout">Logout</Link>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
