import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/style.css";

const Login = () => {
  const [studentId, setStudentId] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (event) => {
    event.preventDefault();

    // Validation: Check if Student ID meets the required format
    const studentIdRegex = /^[A-Za-z0-9]{8}$/; // Example: 8-character alphanumeric
    if (!studentIdRegex.test(studentId)) {
      setError("Invalid Student ID. It must be 8 alphanumeric characters.");
      return;
    }

    // Mock API Call - Backend should validate the Student ID and password
    try {
      const response = await fetch("http://your-backend-api-url/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ studentId, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // If login successful, navigate to student dashboard
        navigate("/student-dashboard");
      } else {
        setError(data.message || "Invalid Student ID or Password.");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h1>Student Login</h1>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label htmlFor="studentId">Student ID</label>
          <input
            type="text"
            id="studentId"
            value={studentId}
            onChange={(e) => setStudentId(e.target.value)}
            placeholder="Enter your Student ID"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your Password"
            required
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="login-btn">Login</button>
      </form>
    </div>
  );
};

export default Login;
