import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../api';  // Importing the API service we created earlier
import './Admin.css';

const AdminLogin = () => {
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/admin/login', credentials);
      // Assuming the response contains a token which we store for future requests
      localStorage.setItem('token', response.data.token);
      alert('Login successful!');
      navigate('/admin/dashboard');  // Navigate to the admin dashboard upon successful login
    } catch (error) {
      setErrorMessage('Invalid credentials');
      console.error("There was an error logging in!", error);
    }
  };

  return (
    <div className="admin-login">
      <h1>Admin Login</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={credentials.username}
          onChange={handleInputChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={credentials.password}
          onChange={handleInputChange}
          required
        />
        <button type="submit">Login</button>
      </form>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default AdminLogin;
