import React, { useState, useEffect } from 'react';
import api from '../../api'; // Adjusted path to fit your structure
import './Admin.css';

const Dashboard = () => {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Example extra state for additional admin functionality
  const [messageCount, setMessageCount] = useState(0);
  const [pendingTasks, setPendingTasks] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const studentResponse = await api.get('/admin/students'); // Fetch students from backend
        setStudents(studentResponse.data);

        // Fetch additional data (these are just example endpoints/actions)
        const messageCountResponse = await api.get('/admin/messages/count');
        setMessageCount(messageCountResponse.data.count);

        const pendingTasksResponse = await api.get('/admin/tasks/pending');
        setPendingTasks(pendingTasksResponse.data.tasks);
      } catch (error) {
        console.error("There was an error fetching dashboard data!", error);
      }
    };

    fetchDashboardData();
  }, []);

  const filteredStudents = students.filter((student) =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.id.includes(searchTerm) ||
    student.class.includes(searchTerm)
  );

  return (
    <div className="dashboard">
      <h1>Admin Dashboard</h1>
      <div className="info-cards">
        <div className="card">
          <h2>Messages</h2>
          <p>{messageCount} unread messages</p>
        </div>
        <div className="card">
          <h2>Pending Tasks</h2>
          <ul>
            {pendingTasks.map((task, index) => (
              <li key={index}>{task.name}</li>
            ))}
          </ul>
        </div>
      </div>
      <h1>Student List</h1>
      <input
        type="text"
        placeholder="Search by name, ID, or class"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <table>
        <thead>
          <tr>
            <th>Student ID</th>
            <th>Name</th>
            <th>Level</th>
            <th>Class</th>
          </tr>
        </thead>
        <tbody>
          {filteredStudents.map((student) => (
            <tr key={student.id}>
              <td>{student.id}</td>
              <td>{student.name}</td>
              <td>{student.level}</td>
              <td>{student.class}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
